// import React, { useState } from 'react';
import DadosPessoais from './Dados Pessoais';
import DadosEntrega from './DadosEntrega';
import DadosUsuario from './DadosUsuario';
import { Typography, Stepper } from '@material-ui/core';
import { useEffect, useState } from 'react';


function FormularioCadastro({ aoEnviar, validaCpf }) {
    const [etapaAtual, setEtapaAtual] = useState(0);
    const [dadosColetados, setDados] = useState({});
    
    useEffect(()=>{
        if(etapaAtual === formularios.length-1){
          aoEnviar(dadosColetados);
        }
      })

    const formularios = [
        <DadosUsuario aoEnviar={coletaDados} />,
        <DadosPessoais aoEnviar={coletaDados} validaCpf={validaCpf} />,
        <DadosEntrega aoEnviar={coletaDados}/>,
    <Typography variant="h5">Obrigado pelo Cadastro!</Typography>
    ];

    function coletaDados(dados) {
        setDados({...dadosColetados, ...dados});
        proximo();
    }


    function proximo() {
        setEtapaAtual(etapaAtual + 1);
    }

    return (
        <>

            {formularios[etapaAtual]}
        </>
    );
}

export default FormularioCadastro;
