import React, {useState} from 'react';
import { Button, TextField, Switch, FormControlLabel } from '@material-ui/core';
import { cpfMask } from './mask'

function FormularioCadastro() {

    const [nome,setNome] = useState("");
    const [sobrenome,setSobrenome] = useState("");
    const [cpf,setCPF] = useState("");
    return (
        <form
            onSubmit={(event) => {
                event.preventDefault();
                console.log(nome, sobrenome, cpf); 
                }}
            >
                    
            <TextField 
                value={nome}
                onChange={(event) => {
                    let tmpNome =  (event.target.value);
                    if(tmpNome.length >=4) {
                        tmpNome = (tmpNome.substr(0,4));
                    }
                    setNome(tmpNome);
                }}
                id="nome"
                label="Nome"
                variant="outlined"
                margin="normal"
                fullWidth
            />

            <TextField
                value={sobrenome}
                onChange={(event) => {
                    setSobrenome(event.target.value);
                }}
                id="sobrenome"
                label="Sobrenome"
                variant="outlined"
                margin="normal"
                fullWidth
            />

            <TextField
                value={cpf}
                onChange={(event) => {
                    setCPF(event.target.value);
                }}
                id="cpf"
                label="CPF"
                variant="outlined"
                margin="normal"
                fullWidth
            />

            <FormControlLabel
                label="Promoções"
                control={
                    <Switch
                        name="promoções"
                        defaultChecked
                        color="primary"
                    />
                }
            />

            <FormControlLabel
                label="Novidades"
                control={
                    <Switch
                        name="novidades"
                        defaultChecked
                        color="primary"
                    />
                }
            />

            <Button
                variant="contained"
                color="primary"
                type="submit">
                Cadastrar
            </Button>
        </form>
    )
}

export default FormularioCadastro;
