// import React, { useState } from 'react';
import DadosPessoais from './Dados Pessoais';
import DadosEntrega from './DadosEntrega';
import DadosUsuario from './DadosUsuario';
import { Typography } from '@material-ui/core';
import { useState } from 'react';


function FormularioCadastro({ aoEnviar, validaCpf }) {
    const [etapaAtual, setEtapaAtual] = useState(0);

    const formulario = [
        <DadosUsuario aoEnviar={proximo} />,
        <DadosPessoais aoEnviar={proximo} validaCpf={validaCpf} />,
        <DadosEntrega />
    ]

    function proximo() {
        setEtapaAtual(etapaAtual + 1);
    }

    function formularioAtual(etapa) {
        switch (etapa) {
            case 0:
                return <DadosUsuario aoEnviar={proximo} />
            case 1:
                return <DadosPessoais aoEnviar={proximo} validaCpf={validaCpf} />
            case 2:
                return <DadosEntrega />
            default:
                return <Typography>Erro ao selecionar formul√°rio</Typography>
        }

    }

    return (
        <>
            {formularioAtual(etapaAtual)}
        </>
    );
}

export default FormularioCadastro;
